import React from "react";
import PageAccueil from "./PageAccueil";
import PageConnexion from "./PageConnexion";
import {UseToken} from "./UseContextFonction";
import {useState, useEffect} from "react"
import SpotifyWebApi from "spotify-web-api-js";
import { BrowserRouter, Navigate, Route, Routes } from "react-router-dom";



const spotify = new SpotifyWebApi();

const Containeur = ()=> {

    const [token, setToken] = useState("");

  useEffect(() => {
    const hash = window.location.hash;
    let tokenLocal = localStorage.getItem("token");

    if (hash) {
        tokenLocal = hash
        .substring(1)
        .split("&")
        .find((element) => element.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      localStorage.setItem("token", tokenLocal);
      console.log("jqfnc:kl",tokenLocal)
      setToken(tokenLocal);
      spotify.setAccessToken(tokenLocal);
    }

    console.log("salut token",tokenLocal);

  }, []);

  const userToken = localStorage.getItem("token")
console.log("salut usertoken", userToken)
  return (
    <main>
      <UseToken.Provider value={{ token,setToken,spotify }}>
        <BrowserRouter>
        <Routes>
            <Route path="/" element={!userToken? <PageConnexion /> : <Navigate replace to="/accueil"/>} />
            <Route path="/accueil" element={userToken? <PageAccueil /> : <Navigate replace to="/"/>} />
        </Routes>
        </BrowserRouter>
      </UseToken.Provider>
    </main>
  );
}


export default Containeur
