import { useEffect, useState } from "react"
import { useContext } from "react"
import { UseToken } from "./UseContextFonction"
import {AiOutlineLogout} from "react-icons/ai"

// import logo from "../medias/logo.png"

const Header =({me, setMe})=>{

    const {spotify,token,setToken} = useContext(UseToken)
    useEffect(()=>{
        spotify.getMe().then((id)=>{
            setMe(id)
            // console.log("id c'est ", id);
}, [token])
    })

    function Logout(){
            setToken("")
             window.localStorage.removeItem("token")
    }


    return(
        <main>
            <div>
                {/* <img src= {logo} alt="le logo de Bleudy-Muzik" /> */}
                <span>B-Muzik</span>
            </div>
            <div>
                {/* <img src={me.images} alt="" /> */}
                <span>{me.display_name} </span>
                <button onClick={Logout} >{AiOutlineLogout} </button>
            </div>
        </main>
    )
}
export default Header
