import React from "react";
// import PageAccueil from "./PageAccueil";
import PageConnexion from "./PageConnexion";
import {UseToken} from "./UseContextFonction";
import {useContext,useState} from "react"

const Containeur = ()=> {

    const spotify = new SpotifyWebApi();


  const [token, setToken] = useState("");

  useEffect(() => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (hash) {
      token = hash
        .substring(1)
        .split("&")
        .find((element) => element.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      window.localStorage.setItem("token", token);
    }
    setToken(token);
    spotify.setAccessToken(token);
  }, [token]);

  return (
    <main>
      <TokenContext.Provider value={{ token,setToken,spotify }}>
        <Routes>

        <Route path="/" element={!token? <ToConnect /> : <Navigate replace to="/Homepage"></Navigate>} />

        <Route path="/Homepage" element={token? <HomePage /> : <Navigate replace to="/"></Navigate>} />

          {/* <Route path="/" element={<ToConnect />} />
          <Route path="/Homepage" element={<HomePage />} /> */}
        </Routes>
      </TokenContext.Provider>
    </main>
  );
}


export default Containeur
