const [token, setToken] = useState("");

  useEffect(() => {

    console.log("Enter useEffect")
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (hash) {
      token = hash
        .substring(1)
        .split("&")
        .find((element) => element.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      window.localStorage.setItem("token", token);
    }
    setToken(token);
    spotify.setAccessToken(token);
  }, [token]);
  return (

    <main>
      <UseToken.Provider value={{ token,setToken,spotify }}>
        <BrowserRouter>
        <Routes>
            <Route path="/" element={!token? <PageConnexion /> : <Navigate replace to="/accueil"/>} />
            <Route path="/accueil" element={token? <PageAccueil /> : <Navigate replace to="/"/>} />
        </Routes>
        </BrowserRouter>
      </UseToken.Provider>
    </main>
  );
}


export default Containeur
