import React from "react";
import PageAccueil from "./PageAccueil";
import PageConnexion from "./PageConnexion";
import {UseToken} from "./UseContextFonction";
import {useContext,useState, useEffect} from "react"
import SpotifyWebApi from "spotify-web-api-js";
import { Navigate, Route, Routes } from "react-router-dom";



const spotify = new SpotifyWebApi();

const Containeur = ()=> {

    const [token, setToken] = useState("");

  useEffect(() => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (hash) {
      token = hash
        .substring(1)
        .split("&")
        .find((element) => element.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      window.localStorage.setItem("token", token);
    }
    setToken(token);
    spotify.setAccessToken(token);
  }, [token]);

  return (
    <main>
      <UseToken.Provider value={{ token,setToken,spotify }}>
        <Routes>
            <Route path="/" element={!token? <PageConnexion /> : <Navigate replace to="/Homepage"></Navigate>} />
        <Route path="/Homepage" element={token? <PageAccueil /> : <Navigate replace to="/"></Navigate>} />
        </Routes>
      </UseToken.Provider>
    </main>
  );
}


export default Containeur
